import React, { useEffect, useState } from 'react';
import axios from 'axios';
import FuelEfficiencyCalculator from "./FuelEfficiencyCalculator"; // Updated import

const App = () => {
    const [predictions, setPredictions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");

    useEffect(() => {
        axios.get('http://localhost:5000/predict')
            .then(response => {
                setPredictions(response.data.predictions);
                setLoading(false);
            })
            .catch(error => {
                console.error("Error fetching predictions:", error);
                setError("Failed to load predictions. Please try again later.");
                setLoading(false);
            });
    }, []);

    return (
        <div className="container">
            <h1>üö¢ Fleet Management Dashboard</h1>

            <h2>üõ† Maintenance Predictions</h2>
            {loading ? <p>Loading predictions...</p> : 
                error ? <p style={{ color: "red" }}>{error}</p> :
                <ul>
                    {predictions.map((prediction, index) => (
                        <li key={index} style={{ color: prediction ? "red" : "green" }}>
                            {prediction ? "‚ö†Ô∏è Maintenance Required" : "‚úÖ No Maintenance Required"}
                        </li>
                    ))}
                </ul>
            }

            <hr />

            <h2>‚õΩ Fuel Efficiency Calculator</h2>
            <FuelEfficiencyCalculator />
        </div>
    );
};

export default App;
